example.code:
public override void DrawInSurface (SKSurface surface, SKImageInfo info)
{
    // call the base method
    base.DrawInSurface (surface, info);

    var surfaceWidth = info.Width;
    var surfaceHeight = info.Height;

    var canvas = surface.Canvas;

    // draw on the canvas
    canvas.Flush ();
}

SKCanvasLayer myLayer = ...;

myLayer.PaintSurface += (sender, e) => {
    var surface = e.Surface;
    var surfaceWidth = e.Info.Width;
    var surfaceHeight = e.Info.Height;

    var canvas = surface.Canvas;

    // draw on the canvas
    canvas.Flush ();
};

protected override void DrawInSurface (SKSurface surface, SKImageInfo info)
{
    // call the base method
    base.DrawInSurface (surface, info);

    var surfaceWidth = info.Width;
    var surfaceHeight = info.Height;

    var canvas = surface.Canvas;

    // draw on the canvas
    canvas.Flush ();
}

